import React from 'react'
import Responses from './Responses';

class Question extends React.Component {
    constructor(props){
        super(props)

        this.state = {
            responses : props.content.responses,
            selectedResponse : props.selectedResponse
        }
    }
    componentWillReceiveProps(nextProps){
        this.setState({
            responses : nextProps.content.responses,
            selectedResponse : nextProps.selectedResponse
        })
        this.forceUpdate()
    }
    componentWillUpdate(){
        document.querySelectorAll('.radio-response-btn').forEach(e => {e.checked = false })

    }
    render(){
    return (
        <div className='question'>
            <h4>{this.props.content.question}</h4>
            <Responses responses = {this.state.responses} selectedResponse = {this.state.selectedResponse}/>
        </div>
    )
}
}

export default Question
