import React, { Component } from 'react'

import CountDown from 'react-countdown-now'

import './Questions.css'
import QuestionCounter from './questionCounter/QuestionCounter';
import Question from './question/Question';
import Confirmation from './Confirmation/Confirmation'

class Questions extends Component {
  constructor(props) {
    super(props)
    let question = [
      {
        question : 'السؤال الاول',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      },
      {
        question : 'السؤال الثاني',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      },
      {
        question : 'السؤال الثالث',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      },
      {
        question : 'السؤال الرابع',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      },
    ]
    this.state = {
      questions: question,
      time: window.localStorage.getItem('time')
        ? window.localStorage.getItem('time')
        : Date.now() + 100000,
      index : 0
    }

    this.onNextButtonClick = this.onNextButtonClick.bind(this)
  }
  countDownRenderer({ minutes, seconds }) {
    return (
      <div className="timer">
        <p> الوقت المتبقي: &nbsp;
          <span>{seconds} : {minutes}</span>
        </p>
      </div>
    )
  }
  onNextButtonClick(){
    this.setState({
      index: this.state.index + 1
    })
  }
  render() {

    let questionSection = this.state.index <= 4 ? <Question content={this.state.questions[this.state.index]}/> : <Confirmation/>


    return (
      <div className="exam-questions">
        <CountDown date={this.state.time} renderer={this.countDownRenderer} />
        <QuestionCounter/>
        {questionSection}
        <button className='btn btn-primary' onClick={this.onNextButtonClick}>التالي</button>
      </div>
    )
  }
}

export default Questions
