import React, { Component } from 'react'

import CountDown from 'react-countdown-now'

import './Questions.css'

import QuestionCounter from './questionCounter/QuestionCounter'
import Question from './question/Question'
import Confirmation from './Confirmation/Confirmation'

class Questions extends Component {
  constructor(props) {
    super(props)
    let question = [
      {
        question: 'السؤال الاول',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      },
      {
        question: 'السؤال الثاني',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      },
      {
        question: 'السؤال الثالث',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      },
      {
        question: 'السؤال الرابع',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      }
    ]

    this.state = {
      questions: question.map((q, k) => {
        return (<Question content={q} index={k}/>)
      }),
      time: window.localStorage.getItem('time')
        ? window.localStorage.getItem('time')
        : Date.now() + 100000,
      index: 0
    }
    console.log(this.state.questions)
    this.onNextButtonClick = this.onNextButtonClick.bind(this)
    this.onPreviousButtonClick = this.onPreviousButtonClick.bind(this)
  }

  countDownRenderer({ minutes, seconds }) {
    return (
      <div className="timer">
        <p>
          {' '}
          الوقت المتبقي: &nbsp;
          <span>
            {seconds} : {minutes}
          </span>
        </p>
      </div>
    )
  }

  onNextButtonClick() {
    this.setState({
      index: this.state.index + 1
    })
  }

  onPreviousButtonClick() {
    this.setState({
      index: this.state.index - 1
    })
  }

  render() {
    let questionSection =
      this.state.index < 4 ? (
        <React.Fragment>{this.state.questions[this.state.index]}</React.Fragment>
      ) : (
        <Confirmation />
      )

    let submission =
      this.state.index === 0 ? (
        <div className="submission">
          <button className="btn btn-primary" onClick={this.onNextButtonClick}>
            التالي
          </button>
        </div>
      ) : this.state.index === this.state.questions.length - 1 ? (
        <div className="submission">
          <button
            className="btn btn-warning"
            onClick={this.onPreviousButtonClick}
          >
            العودة
          </button>
          <button className="btn btn-success" onClick={this.onNextButtonClick}>
            اكمال
          </button>
        </div>
      ) : this.state.index === this.state.questions.length ? (
        <div className="submission">
          <button
            className="btn btn-warning"
            onClick={this.onPreviousButtonClick}
          >
            العودة
          </button>
          <button className="btn btn-success" onClick={this.onNextButtonClick}>
            متأكد
          </button>
        </div>
      ) : (
        <div className="submission">
          <button
            className="btn btn-warning"
            onClick={this.onPreviousButtonClick}
          >
            العودة
          </button>
          <button className="btn btn-primary" onClick={this.onNextButtonClick}>
            التالي
          </button>
        </div>
      )

    return (
      <div className="exam-questions">
        <CountDown date={this.state.time} renderer={this.countDownRenderer} />
        <QuestionCounter totalQuestions = {this.state.questions.length} questionNumber={this.state.index + 1}/>
        {questionSection}
        {submission}
      </div>
    )
  }
}

export default Questions
