import React from 'react'

import { Form, Checkbox } from 'semantic-ui-react'

class Choice extends React.Component {

  constructor(props){
    super(props)

    this.onRadioClick = this.onRadioClick.bind(this)
  }
  onRadioClick(value){
    window.localStorage.setItem(this.props.index, value)
  }

  render() {
    return (
      <div className="form-check card response-box">
          <input type='radio' value={this.props.propValue} name={this.props.index} onClick={(e) => {this.onRadioClick(e.target.value)}}/> {this.props.propValue}
      </div>
    )
  }
}

export default Choice
