import React, { Component } from 'react'

import CountDown from 'react-countdown-now'

import './Questions.css'

import QuestionCounter from './questionCounter/QuestionCounter'
import Question from './question/Question'
import Confirmation from './Confirmation/Confirmation'

class Questions extends Component {
  constructor(props) {
    super(props)
    let question = [
      {
        question: 'السؤال الاول',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      },
      {
        question: 'السؤال الثاني',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      },
      {
        question: 'السؤال الثالث',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      },
      {
        question: 'السؤال الرابع',
        responses: [
          'الجواب الاول',
          'الجواب الثاني',
          'الجواب الثالث',
          'الجواب الرابع'
        ]
      }
    ]

    this.state = {
      questions: question.map((q, k) => {
        return (
          <Question
            content={q}
            index={k}
            selectedResponse={window.localStorage.getItem(this.state.index)}
          />
        )
      }),
      time: window.localStorage.getItem('time')
        ? window.localStorage.getItem('time')
        : Date.now() + 100000,
      index: 0
    }

    this.onNextButtonClick = this.onNextButtonClick.bind(this)
    this.onPreviousButtonClick = this.onPreviousButtonClick.bind(this)
  }

  countDownRenderer({ minutes, seconds }) {
    return (
      <div className="timer">
        <p>
          {' '}
          الوقت المتبقي: &nbsp;
          <span>
            {seconds} : {minutes}
          </span>
        </p>
      </div>
    )
  }

  onNextButtonClick() {
    this.setState({
      index: this.state.index + 1
    })
  }

  onPreviousButtonClick() {
    this.setState({
      index: this.state.index - 1
    })
  }

  onStartExamButtons() {
    return (
      <div className="submission">
        <button className="btn btn-primary" onClick={this.onNextButtonClick}>
          التالي
        </button>
      </div>
    )
  }

  duringExamButtons() {
    return (
      <div className="submission">
        <button
          className="btn btn-warning"
          onClick={this.onPreviousButtonClick}
        >
          العودة
        </button>
        <button className="btn btn-primary" onClick={this.onNextButtonClick}>
          التالي
        </button>
      </div>
    )
  }

  onLastExamQuestionButtons() {
    return (
      <div className="submission">
        <button
          className="btn btn-warning"
          onClick={this.onPreviousButtonClick}
        >
          العودة
        </button>
        <button className="btn btn-success" onClick={this.onNextButtonClick}>
          اكمال
        </button>
      </div>
    )
  }

  onFinishingExamButtons() {
    return (
      <div className="submission">
        <button
          className="btn btn-warning"
          onClick={this.onPreviousButtonClick}
        >
          العودة
        </button>
        <button className="btn btn-success" onClick={this.onNextButtonClick}>
          متأكد
        </button>
      </div>
    )
  }

  getExamButtons(index, length) {
    let buttons
    if (index === 0) {
      buttons = this.onStartExamButtons()
    } else if (index === length - 1) {
      buttons = this.onLastExamQuestionButtons()
    } else if (index === length) {
      buttons = this.onFinishingExamButtons()
    } else {
      buttons = this.duringExamButtons()
    }
    return buttons
  }

  getResponseSelected() {
    let response = window.localStorage.getItem(this.state.index)
    return response
  }

  render() {
    let questionSection =
      this.state.index < 4 ? (
        <React.Fragment>
          {this.state.questions[this.state.index]}
        </React.Fragment>
      ) : (
        <Confirmation />
      )

    let submission = this.getExamButtons(
      this.state.index,
      this.state.questions.length
    )

    return (
      <div className="exam-questions">
        <CountDown date={this.state.time} renderer={this.countDownRenderer} />
        <QuestionCounter
          totalQuestions={this.state.questions.length}
          questionNumber={this.state.index + 1}
        />
        {questionSection}
        {submission}
      </div>
    )
  }
}

export default Questions
