import React from 'react'

import { Form, Radio } from 'semantic-ui-react'

class Choice extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      isChecked : props.checked? true : false
    }
    this.onRadioClick = this.onRadioClick.bind(this)
  }
  onRadioClick(value) {
    window.localStorage.setItem(this.props.index, value)
    this.setState({
      isChecked : true
    })
  }
  componentWillReceiveProps(nextProps){
    if (nextProps.checked){
      this.setState({
        isChecked : nextProps.checked
      })
    }
  }
  componentDidMount(){

  }
  componentDidUpdate(){
    document.querySelectorAll('.radio-response-btn').forEach(e => {e.checked = false })

  }

  onRadioChange(e){
    this.setState({
      isChecked: !this.state.isChecked
    })
  }

  render() {
    return (
      <div className="form-check card response-box">
        <input
          type="radio"
          value={this.props.propValue}
          name={this.props.index}
          onClick={e => {
            this.onRadioClick(e.target.value)
          }}
          className='radio-response-btn'
          checked={this.state.isChecked}
          onChange={() => {
          }}
        />{' '}
        {this.props.propValue}
      </div>
    )
  }
}

export default Choice
