import React from 'react'

import './InformationsInput.css'


class InformationsInput extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            inputValue : props.propValue
        }

        this.onChangeInput = this.onChangeInput.bind(this)
    }

    onChangeInput(e){
        this.setState({
            inputValue : e.target.value
        })
    }

    render(){
        return (
            <div className="informations-input">
                <p className='input-prop'>{this.props.prop}: &nbsp;</p> 
                <input type={this.props.type} readOnly = {!this.props.editable} className='form-control input-prop-value' onChange={this.onChangeInput} value={this.state.inputValue}></input>
            </div>
        )
    }
}

export default InformationsInput
