import React from 'react'

import './Responses.css'
import Choice from './Choice'

const Responses = props => {
  return (
    <div className="responses">
      {props.responses.map((r, k) => {
        let checked = false
        if (window.localStorage.getItem(props.index) === k) {
          checked = true
        }
        return <Choice propValue={r} index = {props.index} key={k} checked={checked}/>
      })}
    </div>
  )
}
export default Responses
